mapper.select:
select mbo, bno, title, writer, regDate, content, noCount, exCount, rxReco , noReco
from p_board_view
where bno = #{bno}

mapper.update:
update p_board_view
set noCount = noCount + 1
where bno = #{bno}

update p_board_view
set exCount = exCount + 1
where bno = #{bno}

update p_board_view
set rxReco = rxReco + 1
where bno = #{bno}

update p_board_view
set noReco = noReco + 1
where bno = #{bno}

update p_board_view
set title = #{title} , writer = #{writer} , content = #{content}
where bno = #{bno}

mapper.select:
select bno, rcontent, rwriter, rregDate, bno, mbo
from p_reply_view
where bno = #{bno}
